    static void dfs(int r, int c, int depth, int direction) {
        if (depth == 3) {
            result++;
            return;
        }
        if(direction == 99){
            for (int i = 0; i < 8; i++) {
                int nr = r + dr[i];
                int nc = c + dc[i];
                if (nr >= 0 && nr < rows && nc >= 0 && nc < cols && grid[nr][nc] == ans[depth + 1]) {
                    dfs(nr, nc, depth + 1, i);
                }
            }
        }else{
            int nr = r + dr[direction];
            int nc = c + dc[direction];
            if (nr >= 0 && nr < rows && nc >= 0 && nc < cols && grid[nr][nc] == ans[depth + 1]) {
                dfs(nr, nc, depth + 1, direction);
            }
        }

    }

    public static char[][] convertToCharArray(String text) {
        String[] lines = text.split("\n");
        rows = lines.length;
        cols = lines[0].length();
        char[][] grid = new char[rows][cols];

        for (int r = 0; r < rows; r++) {
            grid[r] = lines[r].toCharArray();
        }

        return grid;
    }
